mapper.insert:
INSERT INTO milestone_template(
id, template_name, status, remarks, create_by, create_date, update_by, update_date, del_flag,
) VALUES (
#{ id }, #{ templateName }, #{ status.id }, #{ remarks }, #{ createBy }, #{ createDate }, #{ updateBy }, #{ updateDate }, #{ delFlag },
)

mapper.update:
UPDATE milestone_template SET
template_name = #{ templateName }, status = #{ status.id }, remarks = #{ remarks }, update_by = #{ updateBy }, update_date = #{ updateDate },
WHERE id = #{id} and update_date = #{oldUpdateDate}

UPDATE milestone_template SET
update_by = #{updateBy},
update_date = #{updateDate},
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
and update_date = #{oldUpdateDate}

mapper.insert:
INSERT INTO milestone_template(
id, template_name, status, remarks, create_by, create_date, update_by, update_date, del_flag,
) VALUES
(
#{entity.id}, #{entity.templateName}, #{entity.status.id}, #{entity.remarks}, #{entity.createBy}, #{entity.createDate}, #{entity.updateBy}, #{entity.updateDate}, #{entity.delFlag},
)

mapper.update:
UPDATE milestone_template



      when id=#{entity.id} then #{entity.templateName}




      when id=#{entity.id} then #{entity.status.id}




      when id=#{entity.id} then #{entity.remarks}




      when id=#{entity.id} then #{entity.updateBy}




      when id=#{entity.id} then #{entity.updateDate}



WHERE

  id = #{entity.id} and update_date = #{entity.oldUpdateDate}

UPDATE milestone_template



      when id=#{entity.id} then 1




      when id=#{entity.id} then #{entity.updateBy}




      when id=#{entity.id} then #{entity.updateDate}



WHERE

  id = #{entity.id} and update_date = #{entity.oldUpdateDate}
