mapper.insert:
INSERT INTO sys_code_rule(
id, code, name, rule_def, remarks, create_by, create_date, update_by, update_date, del_flag,
) VALUES (
#{ id }, #{ code }, #{ name }, #{ ruleDef }, #{ remarks }, #{ createBy }, #{ createDate }, #{ updateBy }, #{ updateDate }, #{ delFlag },
)

mapper.update:
UPDATE sys_code_rule SET
code = #{ code }, name = #{ name }, rule_def = #{ ruleDef }, remarks = #{ remarks }, update_by = #{ updateBy }, update_date = #{ updateDate },
WHERE id = #{id} and update_date = #{oldUpdateDate}

UPDATE sys_code_rule SET
update_by = #{updateBy},
update_date = #{updateDate},
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
and update_date = #{oldUpdateDate}

mapper.insert:
INSERT INTO sys_code_rule(
id, code, name, rule_def, remarks, create_by, create_date, update_by, update_date, del_flag,
) VALUES
(
#{entity.id}, #{entity.code}, #{entity.name}, #{entity.ruleDef}, #{entity.remarks}, #{entity.createBy}, #{entity.createDate}, #{entity.updateBy}, #{entity.updateDate}, #{entity.delFlag},
)

mapper.update:
UPDATE sys_code_rule



      when id=#{entity.id} then #{entity.code}




      when id=#{entity.id} then #{entity.name}




      when id=#{entity.id} then #{entity.ruleDef}




      when id=#{entity.id} then #{entity.remarks}




      when id=#{entity.id} then #{entity.updateBy}




      when id=#{entity.id} then #{entity.updateDate}



