mapper.insert:
INSERT INTO org_company(
id, code, full_name, parent_id, ids, name, sort, chairman, manager, remarks, create_by, create_date, update_by, update_date, del_flag,
) VALUES (
#{ id }, #{ code }, #{ fullName }, #{ parent.id }, #{ ids }, #{ name }, #{ sort }, #{ chairman.id }, #{ manager.id }, #{ remarks }, #{ createBy }, #{ createDate }, #{ updateBy }, #{ updateDate }, #{ delFlag },
)

mapper.update:
UPDATE org_company SET
code = #{ code }, full_name = #{ fullName }, parent_id = #{ parent.id }, ids = #{ ids }, name = #{ name }, sort = #{ sort }, chairman = #{ chairman.id }, manager = #{ manager.id }, remarks = #{ remarks }, update_by = #{ updateBy }, update_date = #{ updateDate },
WHERE id = #{id} and update_date = #{oldUpdateDate}

UPDATE org_company SET
update_by = #{updateBy},
update_date = #{updateDate},
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
and update_date = #{oldUpdateDate}

